import React, { Component } from "react";
import AllRequests from "./AllRequests.js";
import "./Request.css";

export default class Request extends Component {
  constructor(props) {
    super(props);

    this.state = {
      fridgeRepairRequests: []
    };

    this.addrequest = this.addrequest.bind(this);
    this.deleterequest = this.deleterequest.bind(this);
  }

  addrequest(e) {
    if (this.inputElement !== "") {
      var newRequest = {
        text: this.inputElement,
        key: Date.now()
      };

      this.setState(prevState => {
        prevState.fridgeRepairRequests.concat(newRequest);
      });
    }

    this.inputElement = "";

    console.log(this.state.fridgeRepairRequests);

    e.preventDefault();
  }

  deleterequest(key) {
    var filteredRequests = this.state.fridgeRepairRequests.filter(function(
      request
    ) {
      return request.key !== key;
    });

    this.setState({
      fridgeRepairRequests: filteredRequests
    });
  }

  render() {
    return (
      <div className="todoListMain">
        <div className="header">
          <form onSubmit={this.addrequest}>
            <textarea
              ref={request => (this.inputElement = request)}
              placeholder="Оставьте заявку"
            />
            <button type="submit">Отправить </button>
          </form>
        </div>
        <AllRequests
          entries={this.state.fridgeRepairRequests}
          delete={this.deleterequest}
        />
      </div>
    );
  }
}
