import React, { Component } from "react";
import AllRequests from "./AllRequests.js";
import "./Request.css";
import "./bootstrap/css/bootstrap.min.css";

export default class Request extends Component {
  constructor(props) {
    super(props);

    this.state = {
      fridgeRepairRequests: []
    };

    this.addrequest = this.addrequest.bind(this);
    this.deleterequest = this.deleterequest.bind(this);
  }

  addrequest(e) {
    if (
      this.inputElement.value !== "" &&
      this.name.value !== "" &&
      this.address.value !== "" &&
      this.number.value
    ) {
      var newRequest = {
        text: this.inputElement.value,
        key: Date.now()
      };

      this.setState(prevState => {
        return {
          fridgeRepairRequests: prevState.fridgeRepairRequests.concat(
            newRequest
          )
        };
      });
    }
    var xhr = new XMLHttpRequest();
    xhr.open(
      "GET",
      "https://api.telegram.org/bot667797791:AAEO-pOPrPH3sDPZTNUKnwR7Wt8RJrIDvj4/sendMessage?chat_id=855636692&parse_mode=html&text=Сообщение с сайта:" +
        "%0AИмя: " +
        this.name.value +
        "%0AНомер телефона: " +
        this.number.value +
        "%0AАдрес: " +
        this.address.value +
        "%0AТип услуги: " +
        this.type.value +
        "%0AОписание: " +
        newRequest.text,
      true
    );
    xhr.send();

    this.inputElement.value = "";

    this.name.value = "";

    this.address.value = "";

    this.number.value = "";

    console.log(this.state.fridgeRepairRequests);

    e.preventDefault();
  }

  deleterequest(key) {
    var filteredRequests = this.state.fridgeRepairRequests.filter(function(
      request
    ) {
      return request.key !== key;
    });

    this.setState({
      fridgeRepairRequests: filteredRequests
    });
  }

  render() {
    return (
      <div className="todoListMain">
        <div className="header">
          <form className="container inputs" onSubmit={this.addrequest}>
            <div className="row">
              <div className="col-">
                <input
                  className="input"
                  type="text"
                  placeholder="Ваше имя:"
                  ref={name => (this.name = name)}
                />
              </div>
              <div className="col-">
                <input
                  className="input"
                  type="text"
                  placeholder="Ваше номер телефона:"
                  ref={number => (this.number = number)}
                />
              </div>
              <div className="col-">
                <input
                  placeholder="Ваш адрес:"
                  className="input"
                  type="text"
                  ref={address => (this.address = address)}
                />
              </div>
              <div className="col-">
                <select className="select" ref={type => (this.type = type)}>
                  <option>Ремонт</option>
                  <option>Диагностика</option>
                  <option>Продажа на запчасти</option>
                </select>
              </div>
            </div>
            <div className="row">
              <div className="col-xs">
                <textarea
                  maxLength="70"
                  ref={request => (this.inputElement = request)}
                  placeholder="Оставьте заявку..."
                />
              </div>
            </div>
            <div className="row">
              <div className="col-xs">
                <p>Оставьте адрес, номер телефона, краткое описание поломки</p>
              </div>
            </div>

            <div className="allreq">
              <AllRequests
                entries={this.state.fridgeRepairRequests}
                delete={this.deleterequest}
              />
            </div>
            <div className="row">
              <div className="col-xs">
                <button type="submit">Отправить </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
